package generated_program;

import com.pulumi.Context;
import com.pulumi.Pulumi;
import com.pulumi.core.Output;
import com.pulumi.talos.TalosMachineSecrets;
import com.pulumi.talos.TalosFunctions;
import com.pulumi.talos.inputs.TalosClientConfigurationArgs;
import java.util.List;
