        final var thisTalosClientConfiguration = TalosFunctions.TalosClientConfiguration(TalosClientConfigurationArgs.builder()
            .clusterName("example-cluster")
            .clientConfiguration(thisTalosMachineSecrets.clientConfiguration())
            .nodes("10.5.0.2")
            .build());

    }
}
